class Solution:
    def canPermutePalindrome(self, s: str) -> bool:
        string_dict = {}
        running_count = 0
        for character in s:
            if character not in string_dict: 
                string_dict[character] = False
            else:
                string_dict[character] = not string_dict[character]
            
            running_count += 1 if string_dict[character] == False else -1
        
        return running_count <= 1

if __name__ == "__main__":
    solution = Solution()
    
    test_1 = solution.canPermutePalindrome("aab")
    print(f"'aab' is a permutate palindrome: {test_1}")
    
    test_2 = solution.canPermutePalindrome("carerac")
    print(f"'carerac' is a permutate palindrome: {test_2}")

    test_3 = solution.canPermutePalindrome("code")
    print(f"'code' is a permutate palindrome: {test_3}")

    test4 = solution.canPermutePalindrome("aaa")
    print(f"'aaa' is a permutate palindrome: {test4}")

    x=""
    test5 = solution.canPermutePalindrome(x)
    print(f"'{x}' is a permutate palindrome: {test5}")
    solution.canPermutePalindrome(x)